            if(line != null) line.AddMullions(mullionType, false);
        }
    }
}

Wall curtainWall = new FilteredElementCollector(doc).OfClass(typeof(Wall)).Cast<Wall>().FirstOrDefault(w => w.WallType.Kind == WallKind.Curtain);
MullionType mullionType = new FilteredElementCollector(doc).OfClass(typeof(MullionType)).FirstOrDefault() as MullionType;

if (curtainWall != null && mullionType != null)
{
    CurtainGrid grid = curtainWall.CurtainGrid;
    if (grid != null)
    {
        foreach (ElementId gridLineId in grid.GetVGridLineIds())
        {
            CurtainGridLine line = doc.GetElement(gridLineId) as CurtainGridLine;
            if(line != null) line.AddMullions(mullionType, false);
        }
        foreach (ElementId gridLineId in grid.GetUGridLineIds())
        {
            CurtainGridLine line = doc.GetElement(gridLineId) as CurtainGridLine;
            if(line != null) line.AddMullions(mullionType, false);
        }
    }
}

Level level = new FilteredElementCollector(doc).OfClass(typeof(Level)).FirstOrDefault() as Level;
ElementId conduitTypeId = new FilteredElementCollector(doc).OfClass(typeof(ConduitType)).FirstElementId();
if (level != null && conduitTypeId != ElementId.InvalidElementId)
{
    XYZ startPoint = new XYZ({start_x}, {start_y}, {start_z});
    XYZ endPoint = new XYZ({end_x}, {end_y}, {end_z});
    Conduit conduit = Conduit.Create(doc, conduitTypeId, startPoint, endPoint, level.Id);
    if (conduit != null)
    {
        Parameter diameterParam = conduit.get_Parameter(BuiltInParameter.RBS_CONDUIT_DIAMETER_PARAM);
        if (diameterParam != null && !diameterParam.IsReadOnly)
        {
            diameterParam.Set(UnitUtils.ConvertToInternalUnits({diameter_mm}, UnitTypeId.Millimeters));
        }
    }
}

Level level = new FilteredElementCollector(doc).OfClass(typeof(Level)).FirstOrDefault(l => l.Name == "{level_name}") as Level;
CeilingType ceilingType = new FilteredElementCollector(doc).OfClass(typeof(CeilingType)).FirstOrDefault() as CeilingType;
if (ceilingType != null && level != null)
{
    CurveLoop profile = new CurveLoop();
    profile.Append(Line.CreateBound(new XYZ(0,0,0), new XYZ(10,0,0)));
    profile.Append(Line.CreateBound(new XYZ(10,0,0), new XYZ(10,10,0)));
    profile.Append(Line.CreateBound(new XYZ(10,10,0), new XYZ(0,10,0)));
    profile.Append(Line.CreateBound(new XYZ(0,10,0), new XYZ(0,0,0)));
    Ceiling ceiling = Ceiling.Create(doc, new List<CurveLoop>{profile}, ceilingType.Id, level.Id);
    if (ceiling != null)
    {
        Parameter heightParam = ceiling.get_Parameter(BuiltInParameter.CEILING_HEIGHTABOVELEVEL_PARAM);
        if (heightParam != null && !heightParam.IsReadOnly) heightParam.Set(UnitUtils.ConvertToInternalUnits({height_m}, UnitTypeId.Meters));
    }
}

Level foundationLevel = new FilteredElementCollector(doc).OfClass(typeof(Level)).Cast<Level>().OrderBy(l => l.Elevation).FirstOrDefault();
FloorType slabType = new FilteredElementCollector(doc).OfClass(typeof(FloorType)).Cast<FloorType>().FirstOrDefault(ft => ft.get_Parameter(BuiltInParameter.FLOOR_PARAM_IS_STRUCTURAL).AsInteger() == 1);
if (foundationLevel != null && slabType != null)
{
    // Assuming the user wants to modify the existing type. For a new type, Duplicate() is needed.
    CompoundStructure cs = slabType.GetCompoundStructure();
    if (cs != null && cs.GetFirstCoreLayerIndex() != -1) {
        cs.SetLayerWidth(cs.GetFirstCoreLayerIndex(), UnitUtils.ConvertToInternalUnits({thickness_cm}, UnitTypeId.Centimeters));
        slabType.SetCompoundStructure(cs);
    }

    XYZ p1 = new XYZ(0,0,0); XYZ p2 = new XYZ(10,0,0); XYZ p3 = new XYZ(10,15,0); XYZ p4 = new XYZ(0,15,0);
    CurveLoop profile = new CurveLoop();
    profile.Append(Line.CreateBound(p1,p2));
    profile.Append(Line.CreateBound(p2,p3));
    profile.Append(Line.CreateBound(p3,p4));
    profile.Append(Line.CreateBound(p4,p1));
    Floor.Create(doc, new List<CurveLoop>{profile}, slabType.Id, foundationLevel.Id);
}

List<Pipe> pipes = new FilteredElementCollector(doc).OfClass(typeof(Pipe)).Cast<Pipe>().ToList();
if (pipes.Count >= 2)
{
    Connector c1 = pipes[0].ConnectorManager.UnusedConnectors.Cast<Connector>().FirstOrDefault();
    Connector c2 = pipes[1].ConnectorManager.UnusedConnectors.Cast<Connector>().FirstOrDefault();
    if (c1 != null && c2 != null)
    {
        doc.Create.NewElbowFitting(c1, c2);
    }
}

List<Duct> ducts = new FilteredElementCollector(doc).OfClass(typeof(Duct)).Cast<Duct>().ToList();
if (ducts.Count >= 2)
{
    Connector c1 = ducts[0].ConnectorManager.UnusedConnectors.Cast<Connector>().FirstOrDefault();
    Connector c2 = ducts[1].ConnectorManager.UnusedConnectors.Cast<Connector>().FirstOrDefault();
    if (c1 != null && c2 != null)
    {
        doc.Create.NewTeeFitting(c1, c2);
    }
}

Level level = new FilteredElementCollector(doc).OfClass(typeof(Level)).FirstOrDefault() as Level;
FamilySymbol sprinklerSymbol = new FilteredElementCollector(doc).OfCategory(BuiltInCategory.OST_Sprinklers).WhereElementIsElementType().FirstOrDefault() as FamilySymbol;
if (level != null && sprinklerSymbol != null)
{
    if (!sprinklerSymbol.IsActive) sprinklerSymbol.Activate();
    XYZ placementPoint = new XYZ(UnitUtils.ConvertToInternalUnits({x_m}, UnitTypeId.Meters), UnitUtils.ConvertToInternalUnits({y_m}, UnitTypeId.Meters), UnitUtils.ConvertToInternalUnits({z_m}, UnitTypeId.Meters));
    doc.Create.NewFamilyInstance(placementPoint, sprinklerSymbol, level, Autodesk.Revit.DB.Structure.StructuralType.NonStructural);
}

Duct duct = new FilteredElementCollector(doc).OfClass(typeof(Duct)).FirstOrDefault() as Duct;
FamilySymbol terminalSymbol = new FilteredElementCollector(doc).OfCategory(BuiltInCategory.OST_DuctTerminal).WhereElementIsElementType().FirstOrDefault() as FamilySymbol;
if (duct != null && terminalSymbol != null)
{
    if(!terminalSymbol.IsActive) terminalSymbol.Activate();
    LocationCurve locCurve = duct.Location as LocationCurve;
    if (locCurve != null)
    {
        Connector endConnector = duct.ConnectorManager.UnusedConnectors.Cast<Connector>().FirstOrDefault(c => c.Origin.IsAlmostEqualTo(locCurve.Curve.GetEndPoint(1)));
        if (endConnector != null)
        {
            doc.Create.NewFamilyInstance(endConnector.Origin, terminalSymbol, endConnector);
        }
    }
}
