}

UIDocument uidoc = new UIDocument(doc);
ICollection<ElementId> selectedIds = uidoc.Selection.GetElementIds();
WallType targetWallType = new FilteredElementCollector(doc).OfClass(typeof(WallType)).Cast<WallType>().FirstOrDefault(wt => wt.Name == "Muro Exterior");

if (selectedIds.Count > 0 && targetWallType != null)
{
    foreach(ElementId id in selectedIds)
    {
        Wall wall = doc.GetElement(id) as Wall;
        if(wall != null) wall.ChangeTypeId(targetWallType.Id);
    }
}

using (Transaction t = new Transaction(doc, "Assign Material"))
{
    t.Start();
    WallType wallType = new FilteredElementCollector(doc).OfClass(typeof(WallType)).Cast<WallType>().FirstOrDefault(wt => wt.GetCompoundStructure() != null);
    Material steel = new FilteredElementCollector(doc).OfClass(typeof(Material)).Cast<Material>().FirstOrDefault(m => m.Name.Contains("Steel") || m.Name.Contains("Acero"));

    if (wallType != null && steel != null)
    {
        CompoundStructure cs = wallType.GetCompoundStructure();
        if (cs != null && cs.LayerCount > 0)
        {
            cs.SetMaterialId(0, steel.Id);
            wallType.SetCompoundStructure(cs);
        }
    }
    t.Commit();
}

string viewName = "Vista 3D Sureste";
ViewFamilyType vft = new FilteredElementCollector(doc).OfClass(typeof(ViewFamilyType)).Cast<ViewFamilyType>().FirstOrDefault(v => v.ViewFamily == ViewFamily.ThreeDimensional);

if (vft != null)
{
    View3D view3d = View3D.CreateIsometric(doc, vft.Id);
    if (view3d != null)
    {
        XYZ eye = new XYZ(1, -1, 1);
        XYZ up = new XYZ(0, 0, 1);
        XYZ forward = new XYZ(-1, 1, -1);
        view3d.SetOrientation(new ViewOrientation3D(eye, up, forward));
        view3d.Name = viewName;
    }
}

double elevation = UnitUtils.ConvertToInternalUnits(15.5, UnitTypeId.Meters);
Level newLevel = Level.Create(doc, elevation);

if (newLevel != null)
{
    newLevel.Name = "Nivel Azotea";
}

Level level = new FilteredElementCollector(doc).OfClass(typeof(Level)).FirstOrDefault(l => l.Name == "Level 1") as Level;
WallType wallType = new FilteredElementCollector(doc).OfClass(typeof(WallType)).FirstOrDefault(wt => wt.Name == "Generic - 200mm") as WallType;
XYZ startPoint = new XYZ(0, 0, 0);
XYZ endPoint = new XYZ(UnitUtils.ConvertToInternalUnits(10, UnitTypeId.Meters), UnitUtils.ConvertToInternalUnits(4, UnitTypeId.Meters), 0);
