string folderPath = "C:/exports/";
string fileName = doc.Title + ".nwc";
NavisworksExportOptions options = new NavisworksExportOptions();
options.ExportScope = NavisworksExportScope.Model;
doc.Export(folderPath, fileName, options);

string filePath = "C:/CAD_Files/Base_Plan.dwg";
DWGImportOptions options = new DWGImportOptions();
options.ColorMode = ImportColorMode.Preserved;
ElementId linkedFileId;
View activeView = doc.ActiveView;

if (activeView != null)
{
    doc.Link(filePath, options, activeView, out linkedFileId);
