    Level topLevel = new FilteredElementCollector(doc).OfClass(typeof(Level)).Cast<Level>().OrderByDescending(l => l.ProjectElevation).FirstOrDefault();

    if (bottomLevel != null && topLevel != null)
    {
        XYZ p1 = new XYZ(2 / 0.3048, 2 / 0.3048,0);
        XYZ p2 = new XYZ(5 / 0.3048, 2 / 0.3048,0);
        XYZ p3 = new XYZ(5 / 0.3048, 5 / 0.3048,0);
        XYZ p4 = new XYZ(2 / 0.3048, 5 / 0.3048,0);
        CurveArray profile = new CurveArray();
        profile.Append(Line.CreateBound(p1,p2));
        profile.Append(Line.CreateBound(p2,p3));
