using (Transaction t = new Transaction(doc, "Create Shaft Opening"))
{
    t.Start();
    Level bottomLevel = new FilteredElementCollector(doc).OfClass(typeof(Level)).Cast<Level>().FirstOrDefault(l => l.Name == "Nivel 1");
    Level topLevel = new FilteredElementCollector(doc).OfClass(typeof(Level)).Cast<Level>().FirstOrDefault(l => l.Name == "Nivel 3");

    if (bottomLevel != null && topLevel != null)
    {
        XYZ p1 = new XYZ(5 / 0.3048, 5 / 0.3048,0);
        XYZ p2 = new XYZ(8 / 0.3048, 5 / 0.3048,0);
        XYZ p3 = new XYZ(8 / 0.3048, 8 / 0.3048,0);
        XYZ p4 = new XYZ(5 / 0.3048, 8 / 0.3048,0);
        CurveArray profile = new CurveArray();
        profile.Append(Line.CreateBound(p1,p2));
        profile.Append(Line.CreateBound(p2,p3));
        profile.Append(Line.CreateBound(p3,p4));
        profile.Append(Line.CreateBound(p4,p1));
        doc.Create.NewOpening(bottomLevel, topLevel, profile);
    }
    t.Commit();
}

