{
    XYZ p1 = new XYZ(0,0,0);
    XYZ p2 = new XYZ(UnitUtils.ConvertToInternalUnits(8, UnitTypeId.Meters),UnitUtils.ConvertToInternalUnits(8, UnitTypeId.Meters),0);
    XYZ midPoint = (p1 + p2) / 2;
    XYZ direction = (p2 - p1).Normalize();
    XYZ up = XYZ.BasisZ;
    XYZ viewdir = up.CrossProduct(direction);

    BoundingBoxXYZ sectionBox = new BoundingBoxXYZ();
    Transform t = Transform.Identity;
    t.Origin = midPoint;
t.BasisX = direction;
t.BasisY = up;
t.BasisZ = viewdir;
sectionBox.Transform = t;
sectionBox.Min = new XYZ(UnitUtils.ConvertToInternalUnits(-p1.DistanceTo(p2)/2, UnitTypeId.Meters), UnitUtils.ConvertToInternalUnits(-10, UnitTypeId.Meters), 0);
sectionBox.Max = new XYZ(UnitUtils.ConvertToInternalUnits(p1.DistanceTo(p2)/2, UnitTypeId.Meters), UnitUtils.ConvertToInternalUnits(8, UnitTypeId.Meters), UnitUtils.ConvertToInternalUnits(20, UnitTypeId.Meters));

    ViewSection.CreateSection(doc, vft.Id, sectionBox);
}

