if (stirrupType != null && shape != null && beam != null)
{
    LocationCurve beamLocCurve = beam.Location as LocationCurve;
    if (beamLocCurve != null)
    {
        IList<Curve> curves = new List<Curve>();
        curves.Add(beamLocCurve.Curve);

        XYZ normal = XYZ.BasisZ;
        XYZ origin = beamLocCurve.Curve.GetEndPoint(0);

        Rebar rebar = Rebar.CreateFromCurves(doc, RebarStyle.StirrupTie, stirrupType, null, null, beam, normal, curves, RebarHookOrientation.Right, RebarHookOrientation.Right, true, true);
        if (rebar != null)
