            if (definition != null)
            {
                InstanceBinding binding = doc.Application.Create.NewInstanceBinding(catSet);
                doc.ParameterBindings.Insert(definition, binding, BuiltInParameterGroup.PG_DATA);
            }
        }
    }
}

