{
    DefinitionGroup group = defFile.Groups.get_Item("Documentation");
    if(group == null) group = defFile.Groups.Create("Documentation");

    if (group != null)
    {
        Definition definition = group.Definitions.get_Item(paramName);
        if(definition == null) definition = group.Definitions.Create(new ExternalDefinitionCreationOptions(paramName, SpecTypeId.Url));

        if (definition != null)
        {
            Category wallCat = doc.Settings.Categories.get_Item(BuiltInCategory.OST_Walls);
            if (wallCat != null)
            {
                CategorySet catSet = doc.Application.Create.NewCategorySet();
                catSet.Insert(wallCat);
                InstanceBinding binding = doc.Application.Create.NewInstanceBinding(catSet);
                doc.ParameterBindings.Insert((ExternalDefinition)definition, binding, BuiltInParameterGroup.PG_DATA);
            }
        }
    }
}

