string paramName = "Notas Adicionales";
DefinitionFile defFile = doc.Application.OpenSharedParameterFile();

if (defFile != null)
{
    DefinitionGroup group = defFile.Groups.get_Item("General");
    if(group == null) group = defFile.Groups.Create("General");

    if (group != null)
    {
        Definition definition = group.Definitions.get_Item(paramName);
        if(definition == null) definition = group.Definitions.Create(new ExternalDefinitionCreationOptions(paramName, SpecTypeId.String.Text));

        if (definition != null)
        {
            CategorySet categories = doc.Application.Create.NewCategorySet();
            Category wallsCat = doc.Settings.Categories.get_Item(BuiltInCategory.OST_Walls);
            Category doorsCat = doc.Settings.Categories.get_Item(BuiltInCategory.OST_Doors);
            if (wallsCat != null) categories.Insert(wallsCat);
            if (doorsCat != null) categories.Insert(doorsCat);

            if (categories.Size > 0)
            {
                InstanceBinding binding = doc.Application.Create.NewInstanceBinding(categories);
                doc.ParameterBindings.Insert((ExternalDefinition)definition, binding, BuiltInParameterGroup.PG_DATA);
            }
        }
    }
}

