FamilySymbol columnSymbol = new FilteredElementCollector(doc).OfCategory(BuiltInCategory.OST_StructuralColumns).WhereElementIsElementType().FirstOrDefault(s => s.Name == "Hormigón-Rectangular 30x60") as FamilySymbol;
Level level = new FilteredElementCollector(doc).OfClass(typeof(Level)).FirstOrDefault(l => l.IsGroundPlane) as Level;
List<Grid> grids = new FilteredElementCollector(doc).OfClass(typeof(Grid)).Cast<Grid>().ToList();
if (columnSymbol != null && !columnSymbol.IsActive) columnSymbol.Activate();
if (level != null && columnSymbol != null)
{
    for (int i = 0; i < grids.Count; i++)
    {
        for (int j = i + 1; j < grids.Count; j++)
        {
            IntersectionResultArray results;
            grids[i].Curve.Intersect(grids[j].Curve, out results);
            if (results != null)
            {
                foreach (IntersectionResult ir in results)
                {
                    doc.Create.NewFamilyInstance(ir.XYZPoint, columnSymbol, level, StructuralType.Column);
                }
            }
        }
    }
}

