string paramName = "Notas Adicionales";
DefinitionFile defFile = doc.Application.OpenSharedParameterFile();
if (defFile != null)
{
    DefinitionGroup group = defFile.Groups.get_Item("General") ?? defFile.Groups.Create("General");
    if (group != null)
    {
        Definition definition = group.Definitions.get_Item(paramName) ?? group.Definitions.Create(new ExternalDefinitionCreationOptions(paramName, SpecTypeId.String.Text));
        if (definition != null)
        {
            CategorySet categories = doc.Application.Create.NewCategorySet();
            Category wallCat = doc.Settings.Categories.get_Item(BuiltInCategory.OST_Walls);
            Category doorCat = doc.Settings.Categories.get_Item(BuiltInCategory.OST_Doors);
            if(wallCat != null) categories.Insert(wallCat);
            if(doorCat != null) categories.Insert(doorCat);
            InstanceBinding binding = doc.Application.Create.NewInstanceBinding(categories);
            doc.ParameterBindings.Insert((ExternalDefinition)definition, binding, BuiltInParameterGroup.PG_DATA);
        }
    }
}

