    DefinitionFile sharedParamFile = doc.Application.OpenSharedParameterFile();
    if (sharedParamFile != null)
    {
        DefinitionGroup group = sharedParamFile.Groups.get_Item("CustomParams");
        if (group == null) group = sharedParamFile.Groups.Create("CustomParams");
        if (group != null)
        {
            Definition definition = group.Definitions.get_Item(paramName);
            if (definition == null) definition = group.Definitions.Create(new ExternalDefinitionCreationOptions(paramName, SpecTypeId.String.Text));
            if (definition != null)
            {
                InstanceBinding binding = doc.Application.Create.NewInstanceBinding(catSet);
                doc.ParameterBindings.Insert(definition, binding, BuiltInParameterGroup.PG_IDENTITY_DATA);
            }
        }
    }
}

